mapper.select:
SELECT 

FROM game_news a

WHERE a.id = #{id}

SELECT 

FROM game_news a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.title = #{title}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM game_news a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE game_news SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
